mapper.select:
select sn.*,send_u.username sendUserName,receive_u.username receiveUserName from sys_notice sn left join user send_u on sn.sendUserId=send_u.id left join user receive_u on sn.receiveUserId=receive_u.id where 1=1
and sn.title like CONCAT('%',#{title},'%')
order by createTime desc limit #{rowNum},#{rows}
