mapper.select:
select count(*) from task_bag where 1=1

	and name like CONCAT('%',#{name},'%')


	and projectName like CONCAT('%',#{projectName},'%')


	and uploadUserName like CONCAT('%',#{uploadUserName},'%')


	and createTime between #{createTimeStart} and #{createTimeEnd}


	and state=#{state}

select * from task_bag where 1=1

	and name like CONCAT('%',#{name},'%')


	and projectName like CONCAT('%',#{projectName},'%')


	and uploadUserName like CONCAT('%',#{uploadUserName},'%')


	and createTime between #{createTimeStart} and #{createTimeEnd}


	and state=#{state}

order by createTime desc
limit #{rowNum},#{rows}
