mapper.select:
select count(*) from task_order `to`,task_bag tb,user u where `to`.taskBagId=tb.id and `to`.userId=u.id

	and `to`.no like CONCAT('%',#{no},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{userName},'%')


	and `to`.createTime between #{createTimeStart} and #{createTimeEnd}


	and `to`.finishTime between #{finishTimeStart} and #{finishTimeEnd}


	and `to`.state=#{state}


	and `to`.userId=#{userId}

select `to`.*,tb.name taskBagName,u.userName from task_order `to`,task_bag tb,user u where `to`.taskBagId=tb.id and `to`.userId=u.id

	and `to`.no like CONCAT('%',#{no},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{userName},'%')


	and `to`.createTime between #{createTimeStart} and #{createTimeEnd}


	and `to`.finishTime between #{finishTimeStart} and #{finishTimeEnd}


	and `to`.state=#{state}


	and `to`.userId=#{userId}

order by `to`.createTime desc
limit #{rowNum},#{rows}
