mapper.select:
select count(*) from test_result tr,task_order `to`,task_bag tb,user testu where tr.orderId=`to`.id and tr.testUserId=testu.id and `to`.taskBagId=tb.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.phone like CONCAT('%',#{phone},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.state=#{state}



	 	and testu.id=#{userId}


	 	and `to`.userId=#{userId}

select tr.*,`to`.no orderNo,tb.name taskBagName,testu.username testUserName from test_result tr,task_order `to`,task_bag tb,user testu where tr.orderId=`to`.id and tr.testUserId=testu.id and `to`.taskBagId=tb.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.phone like CONCAT('%',#{phone},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.state=#{state}



	 	and testu.id=#{userId}


	 	and `to`.userId=#{userId}


order by tr.createTime desc
limit #{rowNum},#{rows}
