mapper.select:
select count(*) from test_result tr,task_order `to`,task_bag tb,user u where tr.orderId=`to`.id and tr.testUserId=u.id and `to`.taskBagId=tb.id and `to`.userId=u.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.state=#{state}

select tr.* from test_result tr,task_order `to`,task_bag tb,user u where tr.orderId=`to`.id and tr.testUserId=u.id and `to`.taskBagId=tb.id and `to`.userId=u.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.state=#{state}

order by tr.createTime desc
limit #{rowNum},#{rows}
