mapper.select:
select count(*) from test_result tr,task_order `to`,task_bag tb,user upload_u,user order_u,user agree_u,user testu where tr.orderId=`to`.id and tb.uploadUserId=upload_u.id and `to`.orderUserId=order_u.id and `to`.agreeUserId=agree_u.id and tr.testUserId=testu.id and `to`.taskBagId=tb.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and upload_u.username like CONCAT('%',#{uploadUserName},'%')


	and order_u.username like CONCAT('%',#{orderUserName},'%')


	and agree_u.username like CONCAT('%',#{agreeUserName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.phone like CONCAT('%',#{phone},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.result=#{result}



	 	and testu.id=#{userId}


	 	and `to`.orderUserId=#{userId}

select tr.*,`to`.no orderNo,tb.name taskBagName,upload_u.username uploadUserName,order_u.username orderUserName,agree_u.userName agreeUserName,testu.username testUserName from test_result tr,task_order `to`,task_bag tb,user upload_u,user order_u,user agree_u,user testu where tr.orderId=`to`.id and tb.uploadUserId=upload_u.id and `to`.orderUserId=order_u.id and `to`.agreeUserId=agree_u.id and tr.testUserId=testu.id and `to`.taskBagId=tb.id

	and `to`.no like CONCAT('%',#{orderNo},'%')


	and tb.name like CONCAT('%',#{taskBagName},'%')


	and upload_u.username like CONCAT('%',#{uploadUserName},'%')


	and order_u.username like CONCAT('%',#{orderUserName},'%')


	and agree_u.username like CONCAT('%',#{agreeUserName},'%')


	and u.username like CONCAT('%',#{testUserName},'%')


	and tr.phone like CONCAT('%',#{phone},'%')


	and tr.createTime between #{createTimeStart} and #{createTimeEnd}


	and tr.result=#{result}



	 	and testu.id=#{userId}


	 	and `to`.orderUserId=#{userId}


order by tr.createTime desc
limit #{rowNum},#{rows}
